mapper.select:
SELECT NO, WRITER, TITLE, CONTENT, IP, PATH, ORIGIN, SAVED, CREATED, LASTMODIFIED
  FROM GALLERY
 WHERE NO = #{no}

mapper.update:
UPDATE GALLERY
   SET TITLE = #{title}
   , CONTENT = #{content}
   , PATH = #{path}
   , ORIGIN = #{origin}
   , SAVED = #{saved}
   , LASTMODIFIED = SYSDATE
 WHERE NO = #{no}
